import React, { useState, useEffect } from "react";
import axios from "axios";
import ItemCard from "./components/ItemCard";
import Pagination from "./components/navigation/Pagination";
import RecipeCard from "./components/RecipeCard"; // Importez RecipeCard
import "./css/App.css";

const ITEMS_PER_PAGE = 50;
const TYPE_IDS = [1, 82, 9, 11, 10, 16, 17];

const App = () => {
  const [items, setItems] = useState([]);
  const [totalItems, setTotalItems] = useState(0);
  const [page, setPage] = useState(1);
  const [selectedItemId, setSelectedItemId] = useState(null); // État pour l'ID de l'item sélectionné

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const typeIdParams = TYPE_IDS.map((id) => `typeId=${id}`).join("&");
        const response = await axios.get(
          `https://api.dofusdb.fr/items?$limit=${ITEMS_PER_PAGE}&$skip=${
            (page - 1) * ITEMS_PER_PAGE
          }&${typeIdParams}`
        );

        // Log the full request URL
        console.log(`Request URL: ${response.request.responseURL}`);

        setItems(response.data.data);
        setTotalItems(response.data.total);
      } catch (error) {
        console.error("Error fetching items:", error);
      }
    };

    fetchItems();
  }, [page]);

  const handleRecipeClick = (itemId) => {
    setSelectedItemId(itemId); // Définit l'ID de l'item sélectionné
  };

  const handleCloseRecipe = () => {
    setSelectedItemId(null); // Réinitialise l'ID de l'item sélectionné
  };

  const totalPages = Math.ceil(totalItems / ITEMS_PER_PAGE);

  return (
    <div className="App">
      <h1>Items DofusDB</h1>
      {selectedItemId ? (
        <RecipeCard itemId={selectedItemId} onClose={handleCloseRecipe} />
      ) : (
        <>
          {items.length > 0 ? (
            items.map((item) => (
              <ItemCard
                key={item.id}
                item={item}
                onRecipeClick={handleRecipeClick}
              />
            ))
          ) : (
            <p>Aucun item correspondant aux critères.</p>
          )}
          <Pagination
            page={page}
            totalPages={totalPages}
            onPageChange={setPage}
          />
        </>
      )}
    </div>
  );
};

export default App;
