import React from "react";
import { useParams, Link } from "react-router-dom";
import "../css/RecipeCard.css";

const RecipeCard = () => {
  // Récupérer l'ID depuis l'URL
  const { id } = useParams();

  return (
    <div className="recipe-card">
      <h2>Recette pour l'élément ID: {id}</h2>
      {/* Vous pouvez ajouter ici une logique pour récupérer les détails de l'élément */}
      {/* Par exemple, faire une requête API pour obtenir les détails de la recette */}

      {/* Lien pour revenir à l'accueil */}
      <Link to="/">Retour à l'accueil</Link>
    </div>
  );
};

export default RecipeCard;
