import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "../css/RecipeCard.css";

const RecipeCard = () => {
  const { id } = useParams(); // Récupérer l'ID de l'élément depuis l'URL
  const [recipe, setRecipe] = useState(null); // État pour stocker les données de la recette
  const [loading, setLoading] = useState(true); // État pour gérer le chargement
  const [error, setError] = useState(null); // État pour gérer les erreurs

  useEffect(() => {
    // Fonction pour récupérer les détails de la recette
    const fetchRecipe = async () => {
      try {
        const response = await axios.get(
          `https://api.dofusdb.fr/recipes?resultId=${id}`
        );
        setRecipe(response.data.data[0]); // Stocker les données de la recette
      } catch (error) {
        setError("Erreur lors de la récupération des données de la recette.");
      } finally {
        setLoading(false); // Fin du chargement
      }
    };

    fetchRecipe();
  }, [id]);

  return (
    <div className="recipe-card">
      <h2>Recette pour l'élément ID: {id}</h2>
      {loading && <p>Chargement...</p>}
      {error && <p>{error}</p>}
      {recipe && (
        <div>
          <h3>Quantités</h3>
          <ul>
            {recipe.quantities.map((quantity, index) => (
              <li key={index}>
                Quantité {index + 1}: {quantity}
              </li>
            ))}
          </ul>
        </div>
      )}
      <Link to="/">Retour à l'accueil</Link>
    </div>
  );
};

export default RecipeCard;
