import React, { useState, useEffect } from "react";
import axios from "axios";

const RecipeCard = ({ itemId, onClose }) => {
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await axios.get(
          `https://api.dofusdb.fr/recipes?resultId=${itemId}`
        );
        const recipeData = response.data[0]; // On suppose que la réponse est un tableau avec un seul élément

        if (recipeData) {
          const ingredientRequests = recipeData.ingredientIds.map((id) =>
            axios.get(`https://api.dofusdb.fr/items/${id}`)
          );

          const ingredientResponses = await Promise.all(ingredientRequests);
          const ingredients = ingredientResponses.map(
            (response) => response.data
          );

          setRecipe({
            ...recipeData,
            ingredients: ingredients,
          });
        }
      } catch (err) {
        setError("Erreur lors de la récupération de la recette.");
      } finally {
        setLoading(false);
      }
    };

    fetchRecipe();
  }, [itemId]);

  if (loading) return <p>Chargement...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="recipe-card">
      <h2>Recette pour l'item {itemId}</h2>
      {recipe ? (
        <div>
          <h3>Ingrédients:</h3>
          <ul>
            {recipe.ingredientIds.map((ingredientId, index) => {
              const ingredient = recipe.ingredients.find(
                (item) => item.id === ingredientId
              );
              return ingredient ? (
                <li key={ingredientId}>
                  {ingredient.name.fr} : {recipe.quantities[index]} unité(s)
                </li>
              ) : null;
            })}
          </ul>
          <button onClick={onClose}>Fermer</button>
        </div>
      ) : (
        <p>Aucune recette trouvée.</p>
      )}
    </div>
  );
};

export default RecipeCard;
