import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../css/ItemCard.css";

const ItemCard = ({ item }) => {
  const [price, setPrice] = useState(null);

  useEffect(() => {
    // Fetch the price from the API
    const fetchPrice = async () => {
      try {
        const response = await fetch("http://localhost:5000/prices");
        const data = await response.json();

        // Find the price for the current item
        const itemPrice = data.find((priceItem) => priceItem.id === item.id);

        if (itemPrice) {
          setPrice(itemPrice.price);
        }
      } catch (error) {
        console.error("Erreur lors de la récupération des prix :", error);
      }
    };

    fetchPrice();
  }, [item.id]);

  return (
    <div className="item-card">
      <h3>{item.name.fr}</h3>
      <p>Type: {item.type.name.fr}</p>
      <img src={item.imgset[1].url} alt={item.name.fr} />
      <p>
        <Link to={`/recipe/${item.id}`}>Recette</Link>
      </p>
      {price && <p>Price: {price} Kamas</p>}
    </div>
  );
};

export default ItemCard;
