import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "../css/ItemCard.css";

const ItemCard = ({ item }) => {
  const [price, setPrice] = useState("");

  useEffect(() => {
    const fetchPrice = async () => {
      try {
        const response = await axios.get("http://localhost:5000/prices");
        const itemPrice = response.data.find((p) => p.name === item.name.fr);
        if (itemPrice) {
          setPrice(itemPrice.price);
        }
      } catch (error) {
        console.error("Erreur lors de la récupération du prix:", error);
      }
    };

    fetchPrice();
  }, [item]);

  return (
    <div className="item-card">
      <h3>{item.name.fr}</h3>
      <p>Type: {item.type.name.fr}</p>
      <img src={item.imgset[1].url} alt={item.name.fr} />
      <p>Prix: {price}</p>
      <p>
        <Link to={`/recipe/${item.id}`}>Recette</Link>
      </p>
    </div>
  );
};

export default ItemCard;
