import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const ITEMS_PER_PAGE = 50;
const TYPE_IDS = [1, 82, 9, 11, 10, 16, 17];

const App = () => {
  const [items, setItems] = useState([]);
  const [totalItems, setTotalItems] = useState(0);
  const [page, setPage] = useState(1);

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const typeIdParams = TYPE_IDS.map((id) => `typeId=${id}`).join("&");
        const response = await axios.get(
          `https://api.dofusdb.fr/items?$limit=${ITEMS_PER_PAGE}&$skip=${
            (page - 1) * ITEMS_PER_PAGE
          }&${typeIdParams}`
        );

        // Log the full request URL
        console.log(`Request URL: ${response.request.responseURL}`);

        setItems(response.data.data);
        setTotalItems(response.data.total);
      } catch (error) {
        console.error("Error fetching items:", error);
      }
    };

    fetchItems();
  }, [page]);

  const totalPages = Math.ceil(totalItems / ITEMS_PER_PAGE);

  return (
    <div className="App">
      <h1>Items DofusDB</h1>
      {items.length > 0 ? (
        items.map((item) => (
          <div className="item" key={item.id}>
            <h3>{item.name.fr}</h3>
            <p>Type: {item.type.name.fr}</p>
            <img src={item.imgset[3].url} alt={item.name.fr} />
            <p>
              <a href="localhost:3000">Recette</a>
            </p>
          </div>
        ))
      ) : (
        <p>Aucun item correspondant aux critères.</p>
      )}
      <div className="pagination">
        {page > 1 && (
          <button onClick={() => setPage(page - 1)}>Précédent</button>
        )}
        {[...Array(totalPages).keys()].map((p) => (
          <button
            key={p + 1}
            onClick={() => setPage(p + 1)}
            style={{ fontWeight: p + 1 === page ? "bold" : "normal" }}
          >
            {p + 1}
          </button>
        ))}
        {page < totalPages && (
          <button onClick={() => setPage(page + 1)}>Suivant</button>
        )}
      </div>
    </div>
  );
};

export default App;
