import React, { useState, useEffect } from "react";
import axios from "axios";

const RecipeCard = ({ itemId, onClose }) => {
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        // Remplacez l'URL ci-dessous par l'URL réelle pour obtenir les détails de la recette
        const response = await axios.get(
          `https://api.dofusdb.fr/recipes/${itemId}`
        );
        setRecipe(response.data);
      } catch (err) {
        setError("Erreur lors de la récupération de la recette.");
      } finally {
        setLoading(false);
      }
    };

    fetchRecipe();
  }, [itemId]);

  if (loading) return <p>Chargement...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="recipe-card">
      <h2>Recette pour l'item {itemId}</h2>
      {recipe ? (
        <div>
          <p>Ingredients:</p>
          <button onClick={onClose}>Fermer</button>
        </div>
      ) : (
        <p>Aucune recette trouvée.</p>
      )}
    </div>
  );
};

export default RecipeCard;
