import React from "react";
import { Link } from "react-router-dom";
import "../css/ItemCard.css";

const ItemCard = ({ item, prices }) => {
  const itemPrice = prices.find((priceItem) => priceItem.id === item.id);

  return (
    <div className="item-card">
      <h3>{item.name.fr}</h3>
      <p>Type: {item.type.name.fr}</p>
      <img src={item.imgset[1].url} alt={item.name.fr} />
      <p>
        <Link to={`/recipe/${item.id}`}>Recette</Link>
      </p>
      {itemPrice && <p>Price: {itemPrice.price} Kamas</p>}
    </div>
  );
};

export default ItemCard;
